<?phpfunction build_menu($block, $items){	$key = Request::current()->uri();	$html = '';	foreach($items[$block] as $row)	{		$has_children = !empty($row['childs']);			$html .='<li class="'.($has_children ? 'with-submenu' : '').' '.($row['link'] == '/'.$key ? 'active' : '').'">';		$html .='<a href="'.$row['link'].'">					<i class="'.$row['icon'].'"></i>						<div class="label">'.$row['title'].'</div>					</a>';		if($has_children){			$html .='<nav><ul>';			foreach($row['childs'] as $child)			{				$html .='<li><a href="'.$child['link'].'">'.$child['title'].'</a></li>';			}			$html .='<ul></nav>';		}		$html .= '</li>';	}	return $html;}?><!-- menu #1 --><nav>	<h2>Сайт</h2>								<ul>	<?php	echo build_menu('site', $items);				?>	</ul></nav><!-- /menu -->						<!-- menu #2 --><nav>	<h2>Управление</h2>	<ul>	<?php	echo build_menu('administration', $items);				?>	</ul></nav><!-- /menu -->